package com.self.ddyoung.rice.model;/** * * 序列化和反序列化接口 * * @author <a href="mailto:yihui#500mi.com">yihui</a> * @version 1.0 * @since 2015年6月7日 */public interface ISerializer<T> {    /**     *     * 序列化     *     * @author <a href="mailto:yihui@alibaba-inc.com">yihui</a>     * @version 1.0     * @since 2015年6月5日     * @param object     * @return     */    public byte[] serialize(T object);    /**     *     * 反序列化     *     * @author <a href="mailto:yihui@alibaba-inc.com">yihui</a>     * @version 1.0     * @since 2015年6月5日     * @param clazz     * @param b     * @return     */    public T unserialize(byte[] b);}